sp_widget.script:
(function () {
    // AAYOS Admin Knowledge KB sys_id (hard-coded)
    var kbId = '2a9ad1659725b6d0ec453b90f053af0a';

    data.results = [];
    data.query = (input && input.q) ? ("" + input.q).trim() : "";
    data.kb_sys_id = kbId;

    if (!kbId) {
        data.error = "KB sys_id is not configured.";
        return;
    }

    // Require at least 2 characters
    if (!data.query || data.query.length < 2) {
        return;
    }

    var gr = new GlideRecord("kb_knowledge");
    gr.addActiveQuery();
    gr.addQuery("workflow_state", "published");
    gr.addQuery("kb_knowledge_base", kbId);

    // Search in short_description OR article body
    var qc = gr.addQuery("short_description", "CONTAINS", data.query);
    qc.addOrCondition("text", "CONTAINS", data.query);

    gr.orderByDesc("sys_updated_on");
    gr.setLimit(20);
    gr.query();

    while (gr.next()) {
        data.results.push({
            sys_id: gr.getUniqueValue(),
            number: gr.getValue("number"),
            short_description: gr.getValue("short_description"),
            category: gr.kb_category.getDisplayValue(),
            updated_on: gr.getDisplayValue("sys_updated_on")
        });
    }

    data.hasResults = data.results.length > 0;
})();
