sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    // Only care about updates
    if (current.operation() !== 'update')
        return;

    // If status just changed, handle date fields
    if (current.status.changes()) {

        // When ticket moves to IN PROGRESS, set actual_service_date (only if not already set)
        if (current.status.changesTo('in_progress')) {
            if (!current.actual_service_date) {
                current.actual_service_date = new GlideDateTime();
            }
        }

        // When ticket moves to CLOSED, set closed_date to "now"
        if (current.status.changesTo('closed')) {
            current.closed_date = new GlideDateTime();
        }
    }

})(current, previous);
