sys_script_include.script:
var UpdateCustomerScript = Class.create();
UpdateCustomerScript.prototype = Object.extendsObject(AbstractAjaxProcessor, {

    updateCustomer: function() {
        var customerGR = new GlideRecord('x_1868112_aayos_customer');
        var sys_id = this.getParameter('sys_id');

        if (customerGR.get(sys_id)) {
            customerGR.name = this.getParameter('name');
            customerGR.email = this.getParameter('email');
            customerGR.phone = this.getParameter('phone');
            customerGR.city = this.getParameter('city');
            customerGR.address_line_1 = this.getParameter('address_line_1');
            customerGR.address_line_2 = this.getParameter('address_line_2');
            customerGR.country = this.getParameter('country');
            customerGR.postal_code = this.getParameter('postal_code');
            customerGR.state_province = this.getParameter('state_province');
            customerGR.update(); // Save changes
            return 'success';
        }

        return 'failed';
    }
});
