sys_security_acl.script:
answer = gs.hasRole('aayos_admin');
if (gs.hasRole('aayos_admin')) {
    answer = true;
} else if (gs.hasRole('aayos_technician')) {
    var t = current.ticket;
    if (!t) {
        answer = false;
    } else {
        var gr = new GlideRecord('x_aayos_ticket');
        if (gr.get(t.sys_id)) {
            answer = (gr.assigned_to == gs.getUserID());
        } else {
            answer = false;
        }
    }
} else {
    answer = false;
}
