sysauto_script.script:
// AAYOS Daily Prediction Job
// Runs daily at 2 AM to process predictions for all active vehicles

var engine = new AAYOSPredictionEngine();
var vehicle = new GlideRecord('x_1868112_aayos_vehicle');
vehicle.addQuery('vehicle_status', 'active');
vehicle.query();

var processed = 0;
var errors = 0;

while (vehicle.next()) {
    try {
        engine.processVehicle(vehicle.sys_id);
        processed++;
    } catch (e) {
        gs.error('AAYOS: Error processing vehicle ' + vehicle.vehicle_id + ': ' + e.toString());
        errors++;
    }
}

gs.info('AAYOS: Daily prediction job completed. Processed: ' + processed + ', Errors: ' + errors);
